mapper.select:
SELECT
COUNT(*) FROM LIKES
WHERE POST_LIKE_NO = #{postNo}

mapper.insert:
NSERT INTO mountain_search (
mountain_search_no, user_no, mountain_search_condition, mountain_search_content,
location_no, difficulty_no, altitude_no
) VALUES (
seq_mountain_search_no.NEXTVAL, #{userNo}, #{searchCondition}, #{searchKeyword}, #{locationCategory},
#{altitudeNo}, #{difficultyNo}
);
